import { useState } from "react";
import { FaBars, FaTimes, FaSearch } from "react-icons/fa";
import { Link, useNavigate } from "react-router-dom";
import "../Styles/Navbar.css";

export default function Navbar() {
  const [open, setOpen] = useState(false);
  const [searchActive, setSearchActive] = useState(false);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const toggleMenu = () => setOpen(!open);
  const toggleSearch = () => setSearchActive(!searchActive);

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      navigate(`/buscar?query=${search}`);
      setSearch(""); // limpiar input
      setSearchActive(false); // cerrar input
      setOpen(false); // cerrar sidebar si estaba abierto
    }
  };

  return (
    <>
      <nav className="navbar">
        <h1>Netflix de Libros</h1>

        {/* Contenedor de lupa y buscador */}
        <div className="search-container">
          {/* Botón lupa */}
          <button className="search-toggle" onClick={toggleSearch}>
            <FaSearch />
          </button>

          {/* Caja de búsqueda */}
          <form
            className={`search-box ${searchActive ? "active" : ""}`}
            onSubmit={handleSearchSubmit}
          >
            <input
              type="text"
              placeholder="Buscar libros..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </form>
        </div>

        {/* Botón hamburguesa */}
        <button className="menu-toggle" onClick={toggleMenu}>
          {open ? <FaTimes /> : <FaBars />}
        </button>
      </nav>

      <div className={`sidebar ${open ? "open" : ""}`}>
        <Link to="/" onClick={toggleMenu}>
          Inicio
        </Link>
        <Link to="/sobre-nosotros" onClick={toggleMenu}>
          Sobre Nosotros
        </Link>
        <Link to="/categoria" onClick={toggleMenu}>
          Categorías
        </Link>
        <Link to="/contacto" onClick={toggleMenu}>
          Contacto
        </Link>
      </div>

      {/* Overlay */}
      {open && <div className="overlay" onClick={toggleMenu}></div>}
    </>
  );
}
