import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        const res = await fetch("http://localhost:5072/api/usuario/login", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ email, password }),
        });

        if (!res.ok) return alert("Credenciales inv치lidas");

        const usuario = await res.json();
        localStorage.setItem("usuario", JSON.stringify(usuario));
        navigate("/");
    };

    const handleGoogleLogin = async () => {

        const googleEmail = "usuario@gmail.com"; 
        const res = await fetch("http://localhost:5072/api/usuario/login-google", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ email: googleEmail, nombre: "Usuario Google" }),
        });
        const usuario = await res.json();
        localStorage.setItem("usuario", JSON.stringify(usuario));
        navigate("/");
    };

    return (
        <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: "5rem" }}>
            <h1>Login</h1>
            <form onSubmit={handleLogin} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
                <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
                <input type="password" placeholder="Contrase침a" value={password} onChange={e => setPassword(e.target.value)} />
                <button type="submit">Iniciar sesi칩n</button>
            </form>
            <button onClick={handleGoogleLogin} style={{ marginTop: "1rem" }}>Iniciar con Google</button>
        </div>
    );
}
