import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import "../Styles/LibroDetalle.css";

export default function LibroDetalle() {
  const { id } = useParams();
  const [libro, setLibro] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5072/api/Libros/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Error al obtener libro");
        return res.json();
      })
      .then((data) => {
        setLibro(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="cargando">Cargando libro...</div>;
  if (!libro) return <div className="cargando">Libro no encontrado</div>;

  return (
    <>
      <Navbar />
      <div className="libro-detalle">
        <h1 className="titulo">{libro.titulo}</h1>
        <h3 className="autor">Autor: {libro.autor}</h3>
        <p className="descripcion">{libro.descripcion}</p>

        {libro.urlPdf && (
          <div className="pdf-container">
            <iframe
              src={libro.urlPdf}
              title={libro.titulo}
              className="pdf-viewer"
            ></iframe>
          </div>
        )}
      </div>
    </>
  );
}
