import { useState } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import "../Styles/Navbar.css";

export default function Navbar() {
  const [open, setOpen] = useState(false);
  const toggleMenu = () => setOpen(!open);

  return (
    <>
      <nav className="navbar">
        <button className="menu-toggle" onClick={toggleMenu}>
          {open ? <FaTimes /> : <FaBars />}
        </button>
        <h1>Netflix de Libros</h1>
      </nav>

      <div className={`sidebar ${open ? "open" : ""}`}>
        <a href="/" onClick={toggleMenu}>Inicio</a>
        <a href="/sobre-nosotros" onClick={toggleMenu}>Sobre Nosotros</a>
        <a href="/categoria" onClick={toggleMenu}>Categor√≠as</a>
        <a href="/contacto" onClick={toggleMenu}>Contacto</a>
      </div>

      {open && <div className="overlay" onClick={toggleMenu}></div>}
    </>
  );
}
