import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import BookCard from "../components/BookCard";
import "../Styles/PaginaPrincipal.css";

export default function Categorias() {
  const [categorias, setCategorias] = useState([]);
  const [librosPorCategoria, setLibrosPorCategoria] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5072/api/Categorias")
      .then((res) => res.json())
      .then((data) => {
        setCategorias(data);

        data.forEach((categoria) => {
          fetch(`http://localhost:5072/api/Categorias/${categoria.id}/libros`)
            .then((res) => res.json())
            .then((catData) => {
              setLibrosPorCategoria((prev) => ({
                ...prev,
                [categoria.id]: catData.libros || catData.Libros || [],
              }));
            });
        });

        setLoading(false);
      })
      .catch((err) => {
        console.error("Error al cargar categor√≠as:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="cargando">Cargando categor√≠as...</div>;

  return (
    <main className="pagina-principal">
      <h1 className="titulo">üìñ Categor√≠as</h1>

      {categorias.map((categoria) => (
        <section key={categoria.id} className="categoria-seccion">
          <h2 className="categoria-nombre">{categoria.nombre}</h2>
          <div className="grid-categoria">
            {librosPorCategoria[categoria.id]?.length > 0 ? (
              librosPorCategoria[categoria.id].map((libro) => (
                <Link
                  key={libro.id}
                  to={`/libro/${libro.id}`}
                  className="book-link"
                >
                  <BookCard
                    title={libro.titulo}
                    author={libro.autor}
                    cover={libro.urlPortada}
                  />
                </Link>
              ))
            ) : (
              <p className="sin-libros">No hay libros en esta categor√≠a.</p>
            )}
          </div>
        </section>
      ))}
    </main>
  );
}
