import React from "react";
import "../Styles/";

export default function BookCard({ title, author, cover, pdf }) {
  return (
    <div className="book-card">
      <img src={cover} alt={title} />
      <div className="book-info">
        <h3>{title}</h3>
        <p>{author}</p>
        {pdf && (
          <a href={pdf} target="_blank" rel="noopener noreferrer" className="pdf-button">
            Ver PDF
          </a>
        )}
      </div>
    </div>
  );
}
