// src/pages/Login.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { GoogleLogin, GoogleOAuthProvider } from "@react-oauth/google";
import "../Styles/Login.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // Aquí podrías hacer fetch a tu backend para login normal
    // Por ahora lo simulamos guardando el usuario en localStorage
    const usuario = { email };
    localStorage.setItem("usuario", JSON.stringify(usuario));
    navigate("/");
  };

  const handleGoogleLoginSuccess = (credentialResponse) => {
    // credentialResponse contiene el token de Google
    // Podés enviarlo al backend para validar o usar directamente
    const usuarioGoogle = {
      nombre: credentialResponse.profileObj?.name || "Usuario Google",
      email: credentialResponse.profileObj?.email || "",
      token: credentialResponse.credential,
    };
    localStorage.setItem("usuario", JSON.stringify(usuarioGoogle));
    navigate("/");
  };

  const handleGoogleLoginError = () => {
    console.log("Login con Google fallido");
  };

  return (
    <div className="login-container">
      <h1>Netflix de Libros</h1>

      <form className="login-form" onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Iniciar sesión</button>
      </form>

      <div className="divider">o</div>

      <GoogleOAuthProvider clientId="TU_CLIENT_ID_DE_GOOGLE">
        <GoogleLogin
          onSuccess={handleGoogleLoginSuccess}
          onError={handleGoogleLoginError}
        />
      </GoogleOAuthProvider>
    </div>
  );
}
