import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom'; // si usas React Router

function Login() {
  const navigate = useNavigate(); // solo si usas React Router

  const handleLoginSuccess = (credentialResponse) => {
    console.log("Credencial de Google:", credentialResponse);

    // Mandás el token al backend para validar/crear usuario
    fetch("http://localhost:5000/api/usuarios/google-login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ token: credentialResponse.credential })
    })
    .then(res => res.json())
    .then(data => {
      // Guardás info del usuario en localStorage o Context
      localStorage.setItem("user", JSON.stringify(data));

      // Redirigir a la página principal
      navigate("/home"); // si usas React Router
      // o window.location.href = "/home"; si no usas router
    });
  };

  const handleLoginFailure = () => {
    console.log("Error al iniciar sesión con Google");
  };

  return (
    <div>
      <h2>Iniciar sesión</h2>
      <GoogleLogin
        onSuccess={handleLoginSuccess}
        onError={handleLoginFailure}
      />
    </div>
  );
}

export default Login;
