import React, { useEffect, useState } from "react";
import BookCard from "../components/BookCard";
import "../Styles/PaginaPrincipal.css";

export default function PaginaPrincipal() {
  const [libros, setLibros] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:5072/api/Libros") // tu backend
      .then((res) => {
        if (!res.ok) throw new Error("Error en la respuesta del servidor");
        return res.json();
      })
      .then((data) => {
        setLibros(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error al obtener libros:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="cargando">Cargando libros...</div>;

  return (
    <main>
      <h1 className="titulo">Libros disponibles</h1>
      <div className="grid">
        {libros.map((libro) => (
          <BookCard
          key={libro.Id}
          title={libro.Titulo}
          author={libro.Autor}
          cover={libro.UrlPortada}
          pdf={libro.UrlPdf} // <-- pasamos la URL del PDF
          />
        ))}
      </div>
    </main>
  );
}
