import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../components/Navbar";
import "../Styles/LibroDetalle.css";

export default function LibroDetalle() {
  const { id } = useParams();
  const [libro, setLibro] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5072/api/Libros/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Error al obtener el libro");
        return res.json();
      })
      .then((data) => {
        setLibro(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="cargando">Cargando libro...</div>;
  if (!libro) return <div className="cargando">Libro no encontrado</div>;

  return (
    <>
      <Navbar />
      <main className="libro-detalle-container">
        <div className="libro-header">
          <h1>{libro.titulo}</h1>
          <h3>{libro.autor}</h3>
        </div>
        <div className="libro-descripcion">{libro.descripcion}</div>
        <div className="pdf-container">
          <iframe
            src={`http://localhost:5072${libro.urlPdf}`}
            title={libro.titulo}
            className="pdf-viewer"
          ></iframe>
        </div>
      </main>
    </>
  );
}
