import React from "react";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import "../Styles/Login.css";

export default function Login() {
  const navigate = useNavigate();

  const handleGoogleSuccess = (response) => {
    const usuarioData = {
      nombre: response.profileObj.name,
      email: response.profileObj.email,
      imagen: response.profileObj.imageUrl,
    };

    localStorage.setItem("usuario", JSON.stringify(usuarioData));
    navigate("/"); // Redirige a la página principal
  };

  const handleGoogleFailure = (error) => {
    console.error("Error al iniciar sesión con Google:", error);
    alert("No se pudo iniciar sesión con Google. Intenta de nuevo.");
  };

  const handleManualLogin = (e) => {
    e.preventDefault();
    // Ejemplo simple de login manual
    const nombre = e.target.nombre.value.trim();
    const email = e.target.email.value.trim();

    if (!nombre || !email) {
      alert("Completa todos los campos");
      return;
    }

    const usuarioData = { nombre, email };
    localStorage.setItem("usuario", JSON.stringify(usuarioData));
    navigate("/");
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Iniciar Sesión</h1>

        <form onSubmit={handleManualLogin}>
          <input type="text" name="nombre" placeholder="Nombre" />
          <input type="email" name="email" placeholder="Email" />
          <button type="submit">Iniciar Sesión</button>
        </form>

        <div className="separator">o</div>

        <GoogleLogin
          clientId="TU_CLIENT_ID_DE_GOOGLE"
          buttonText="Iniciar sesión con Google"
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
          cookiePolicy={"single_host_origin"}
        />
      </div>
    </div>
  );
}
