import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // Para leer el id de la URL
import Navbar from "../components/Navbar";
import "../Styles/LibroDetalle.css";

export default function LibroDetalle() {
  const { id } = useParams();
  const [libro, setLibro] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5072/api/Libros/${id}`)
      .then(res => res.json())
      .then(data => {
        setLibro(data);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  if (loading) return <div className="cargando">Cargando...</div>;
  if (!libro) return <div className="cargando">Libro no encontrado</div>;

  return (
    <>
      <Navbar />
      <div className="libro-detalle">
        <h1 className="titulo">{libro.titulo}</h1>
        <h3 className="autor">Autor: {libro.autor}</h3>
        <p className="descripcion">{libro.descripcion}</p>
        {libro.urlPdf && (
          <div className="pdf-container">
            <iframe src={libro.urlPdf} className="pdf-viewer" title={libro.titulo}></iframe>
          </div>
        )}
      </div>
    </>
  );
}
